import { Injectable } from '@angular/core';
import {HttpClient} from '@angular/common/http'
import User from '../models/user';
import { Message } from '../models/message';

@Injectable({
  providedIn: 'root'
})
export class UserService {

  constructor(private http: HttpClient) { }

  uri = "http://localhost:4000/user";

  registerUcenik(user:User)  {
      return this.http.post<Message>('${this.uri}/registerUcenik', user);
    }

    registerNastavnik(user: User){

        return this.http.post<Message>('${this.uri}/registerNastavnik', user);
      }

  postojeciKorisnik(korisnickoIme: string, imejl: string) {
    const data = {
      korisnickoIme: korisnickoIme,
      imejl: imejl
    }
    return this.http.post<Message>('${this.uri}/postojeciKorisnik', data);
  }


}
